// Config Placeholders
const targetDate = new Date("{{TARGET_DATE}}").getTime();
const actionLink = "{{ACTION_LINK}}"; // Optional Link
const btnText = "{{BUTTON_TEXT}}"; // Optional Text

function updateTimer() {
    const now = new Date().getTime();
    const diff = targetDate - now;

    // WHEN COUNTDOWN FINISHES
    if (diff <= 0) {
        clearInterval(window.bannerTimer);
        const el = document.getElementById('updateCountdown');
        if(el) {
            let finishHTML = "<div style='display:flex; flex-direction:column; align-items:center; gap:8px;'>";
            finishHTML += "<h2 style='color:var(--success); margin:5px 0;'>üî• LIVE NOW!</h2>";

            // Logic: Agar link available hai, tabhi button dikhana
            if(actionLink && actionLink.trim() !== "") {
                finishHTML += `<button class='countdown-action-btn' onclick=\"window.open('${actionLink}', '_blank')\">${btnText || 'Open Now'}</button>`;
            }
            finishHTML += "</div>";

            el.innerHTML = finishHTML;
        }
        // Hide the subtitle message when finished
        const msg = document.getElementById('countdownMessage');
        if(msg) msg.style.display = 'none';
        return;
    }

    // NORMAL COUNTDOWN LOGIC
    const d = Math.floor(diff / (1000 * 60 * 60 * 24));
    const h = Math.floor((diff % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
    const m = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));
    const s = Math.floor((diff % (1000 * 60)) / 1000);

    const pad = (n) => String(n).padStart(2, '0');

    if(document.getElementById('days')) {
        document.getElementById('days').innerText = pad(d);
        document.getElementById('hours').innerText = pad(h);
        document.getElementById('minutes').innerText = pad(m);
        document.getElementById('seconds').innerText = pad(s);
    }
}

if (window.bannerTimer) clearInterval(window.bannerTimer);
window.bannerTimer = setInterval(updateTimer, 1000);
updateTimer();

window.toggleAudio = function() {
    const video = document.getElementById('advVideoPlayer');
    const icon = document.getElementById('muteIcon');

    if (video.muted) {
        video.muted = false;
        icon.classList.remove('fa-volume-xmark');
        icon.classList.add('fa-volume-high');
    } else {
        video.muted = true;
        icon.classList.remove('fa-volume-high');
        icon.classList.add('fa-volume-xmark');
    }
}
