// Config Placeholders
const targetDate = new Date("{{TARGET_DATE}}").getTime();
const actionLink = "{{ACTION_LINK}}"; // Optional Link
const btnText = "{{BUTTON_TEXT}}"; // Optional Text

function updateTimer() {
    const now = new Date().getTime();
    const diff = targetDate - now;

    // WHEN COUNTDOWN FINISHES
    if (diff <= 0) {
        clearInterval(window.bannerTimer);
        const el = document.getElementById('updateCountdown');
        if(el) {
            let finishHTML = "<div style='display:flex; flex-direction:column; align-items:center; gap:8px;'>";
            finishHTML += "<h2 style='color:var(--success); margin:5px 0;'>üî• LIVE NOW!</h2>";

            // Logic: Agar link available hai, tabhi button dikhana
            if(actionLink && actionLink.trim() !== "") {
                finishHTML += `<button class='countdown-action-btn' onclick=\"window.open('${actionLink}', '_blank')\">${btnText || 'Open Now'}</button>`;
            }
            finishHTML += "</div>";

            el.innerHTML = finishHTML;
        }
        // Hide the subtitle message when finished
        const msg = document.getElementById('countdownMessage');
        if(msg) msg.style.display = 'none';
        return;
    }

    // NORMAL COUNTDOWN LOGIC
    const d = Math.floor(diff / (1000 * 60 * 60 * 24));
    const h = Math.floor((diff % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
    const m = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));
    const s = Math.floor((diff % (1000 * 60)) / 1000);

    const pad = (n) => String(n).padStart(2, '0');

    if(document.getElementById('days')) {
        document.getElementById('days').innerText = pad(d);
        document.getElementById('hours').innerText = pad(h);
        document.getElementById('minutes').innerText = pad(m);
        document.getElementById('seconds').innerText = pad(s);
    }
}

if (window.bannerTimer) clearInterval(window.bannerTimer);
window.bannerTimer = setInterval(updateTimer, 1000);
updateTimer();

(function() {
    const key = "{{LOCAL_KEY}}";
    if (localStorage.getItem(`adv_rev_${key}`) === "done") {
        const b = document.getElementById(`review_container_${key}`);
        if (b) b.style.display = 'none';
        return;
    }
    const fbConfig = { apiKey: "AIzaSyDfF9zjiBh7zRMxVIYO8IZHx6zpscjayKw", authDomain: "advora-oauth.firebaseapp.com", databaseURL: "https://advora-oauth-default-rtdb.firebaseio.com", projectId: "advora-oauth", storageBucket: "advora-oauth.firebasestorage.app", messagingSenderId: "541266572021", appId: "1:541266572021:web:c7405c368350efd3b94bbc" };
    let ratingMap = {};
    window.setRating = (k, v) => {
        ratingMap[k] = v;
        const bar = document.getElementById(`star_bar_${k}`);
        if(bar) bar.classList.remove('error-shake');
        document.querySelectorAll(`#review_container_${k} .rev-star`).forEach(s => s.classList.toggle('active', s.dataset.v <= v));
    };
    window.dismissBanner = (k) => { document.getElementById(`review_container_${k}`).style.display = 'none'; };
    window.pushReview = async (k) => {
        const btn = document.getElementById(`btn_${k}`);
        const rating = ratingMap[k] || 0;
        const bar = document.getElementById(`star_bar_${k}`);
        if (rating === 0) {
            if(bar) bar.classList.add('error-shake');
            return;
        }
        btn.innerText = "Saving..."; btn.disabled = true;
        try {
            if (typeof firebase === 'undefined') {
                const l = (s) => new Promise(r => { const sc = document.createElement('script'); sc.src = s; sc.onload = r; document.head.appendChild(sc); });
                await l("https://www.gstatic.com/firebasejs/9.1.1/firebase-app-compat.js");
                await l("https://www.gstatic.com/firebasejs/9.1.1/firebase-database-compat.js");
            }
            if (!firebase.apps.length) firebase.initializeApp(fbConfig);
            const u = localStorage.getItem("advoraUser");
            let uid = "anon";
            try { if(u) uid = JSON.parse(u).uid || u; } catch(e) { uid = u; }
            await firebase.database().ref(`app_reviews/${k}`).push({ uid, stars: rating, msg: document.getElementById(`input_${k}`).value, time: new Date().toLocaleString() });
            localStorage.setItem(`adv_rev_${k}`, "done");
            document.getElementById(`content_wrap_${k}`).innerHTML = "<h3 style='color:white;text-align:center;'>‚úÖ Saved!</h3>";
            setTimeout(() => window.dismissBanner(k), 2000);
        } catch (e) { btn.innerText = "Error! Try Again"; btn.disabled = false; }
    };
})();

window.toggleAudio = function() {
    const video = document.getElementById('advVideoPlayer');
    const icon = document.getElementById('muteIcon');

    if (video.muted) {
        video.muted = false;
        icon.classList.remove('fa-volume-xmark');
        icon.classList.add('fa-volume-high');
    } else {
        video.muted = true;
        icon.classList.remove('fa-volume-high');
        icon.classList.add('fa-volume-xmark');
    }
}

window.toggleSmartAudio = function() {
    const video = document.getElementById('smartVideoPlayer');
    const icon = document.getElementById('smartMuteIcon');
    if (video.muted) {
        video.muted = false;
        icon.classList.remove('fa-volume-xmark');
        icon.classList.add('fa-volume-high');
    } else {
        video.muted = true;
        icon.classList.remove('fa-volume-high');
        icon.classList.add('fa-volume-xmark');
    }
}
